import React, { Component } from 'react';
import Cities from './cities';
import {Button, Columns, Column} from 'bloomer';
import ReactDOM from 'react-dom';


export class Search extends Component {
    constructor() {
        super();
        this.state = {
          showHideFName: true,
          showHideLName: true
        };
        this.hideComponent = this.hideComponent.bind(this);
      }
      hideComponent(name) {
        switch (name) {
          case "showHideFName":
            this.setState({ showHideFName: !this.state.showHideFName });
            break;
          case "showHideLName":
            this.setState({ showHideLName: !this.state.showHideLName });
            break;
          default:
            return null;
        }
      }
    render() {
        const { showHideFName, showHideLName } = this.state;
        return(
            <div>
                <h2>Looking for help? Search for a provider near you</h2>
                <Columns isCentered>
                <Column><Cities/></Column>
                <Column><Button>Go</Button></Column>
                </Columns>
                <div>
        <table>
          {showHideFName && (
            <tr>
              <td>First Name :</td>
              <td>
                <input type="text" id="fName" />
              </td>
            </tr>
          )}
          {showHideLName && (
            <tr>
              <td>Last Name :</td>
              <td>
                <input type="text" id="lName" />
              </td>
            </tr>
          )}
          {showHideFName && showHideLName && (
            <tr>
              <td>
                <button>Submit</button>
              </td>
            </tr>
          )}
          <tr>
            <td>
              <button onClick={() => this.hideComponent("showHideFName")}>
                Hide First Name
              </button>
            </td>
            <td>
              <button onClick={() => this.hideComponent("showHideLName")}>
                Hide Last Name
              </button>
            </td>
          </tr>
        </table>
      </div>
            </div>
        );
    }
}
